getDefaultPeer() {
    if (defaultPeer == undefined) {
      var org = this.getDefaultOrg();
      var orgObj = config["network-config"][org];
      var orgKey = Object.keys(orgObj);
      var index = orgKey.indexOf("peer1");
      defaultPeer = orgKey[index];
