{code}
type PKCS11Opts struct {                                                                                                   
  // Default algorithms when not specified (Deprecated?)                                                                   
  SecLevel   int    `mapstructure:"security" json:"security"`                                                              
  HashFamily string `mapstructure:"hash" json:"hash"`                                                                      
                                                                                                                           
