class Sawtooth {

    public var address: String // Node address

    public var port: String // Proxy port

    private var urlString: String // Full rest through proxy url

    private var key: PrivateKey // BitcoinKit.PrivateKey

    public var errorList = ""

    public var familyName: String

    public var familyVersion: String

    

    /** Generates new private key

     */

    static func getKeyGenerated() -> PrivateKey {

        return PrivateKey(network: .mainnet)

    }

    

    /** Restore private key from WIF format

     */

    static func getKeyFromWif(wif: String) throws -> PrivateKey {

        return try PrivateKey.init(wif: wif)

    }

    

    /** Returns private key in WIF format

     */

    static func getWifFromKey(key: PrivateKey) -> String {

        return key.toWIF()

    }

    

    init(address: String, port: String, key: PrivateKey, familyName: String, familyVersion: String) {

        self.address = address

        self.port = port

        self.key = key

        self.urlString = "http://" + address + ":" + port + "/batches"

        self.familyName = familyName

        self.familyVersion = familyVersion

    }

    

    func getAddress(seed: String) -> String {

        let hash = sha512Hex(string: seed)

        let prefix = String(sha512Hex(string: familyName).prefix(6))

        let subhash = String(hash[String.Index(encodedOffset: 64)...])

        return prefix + subhash

    }

    

    /** Makes SHA512 hash of the input string and pins to blockchain.

     */

    public func pin(toHash: String) -> String {

        // Make hash of input data, wrap it into dictionary & calculate it's address

        let hash = sha512Hex(string: toHash)

        let payload = ["Verb":"pin", "Message":hash]

        let address = getAddress(seed: hash)

        

        // Assebmle batchlist

        let batches = getBatchList(payload: payload, address:address)

        

        // do post request and return response

        let response = postBatchList(batchList: batches)

        return response

    }

    

    private func postBatchList(batchList: Data) -> String {

        // Specify http params

        var request = URLRequest(url: URL(string: urlString)!)

        request.httpMethod = "POST"

        request.addValue("application/octet-stream", forHTTPHeaderField: "content-type")

        request.httpBody = batchList

        

        // Do request and record responce

        var result = ""

        let task = URLSession.shared.dataTask(with: request) { data, response, error in

            guard let data = data, error == nil else {                                                 // check for fundamental networking error

                result += ("error=\(error)")

                return

            }

            if let httpStatus = response as? HTTPURLResponse, httpStatus.statusCode != 200 {           // check for http errors

                result += ("statusCode should be 200, but is \(httpStatus.statusCode)")

                result += ("response = \(response)")

            }

            let responseString = String(data: data, encoding: .utf8)

            result += ("responseString = \(responseString)")

        }

        task.resume()

        

        // Return response

        return result

    }


private func getBatchList(payload: [String:String], address: String) -> Data {

        //

        // TODO console log for Protobuf exceptions

        //

        let encodedPayload = Data(CBOR.encode(payload))

        var txHeader = Sawtooth_Transaction_TransactionHeader()

        txHeader.batcherPublicKey = key.publicKey().getRaw().hexEncodedString()

        txHeader.dependencies = [""]

        txHeader.familyName = familyName

        txHeader.familyVersion = familyVersion

        txHeader.inputs = [address] //address from which transaction processor will be able to read

        txHeader.outputs = [address] //address to which transaction processor will be able to write

        txHeader.nonce = String(format:"%f", NSTimeIntervalSince1970)

        txHeader.payloadSha512 = sha512Hex(data: encodedPayload)! //TODO check

txHeader.signerPublicKey = key.publicKey().description

        var txHeaderData: Data = Data()

        do { try txHeaderData = txHeader.serializedData() }

        catch { errorList += ("Error: \(error)" + "\n"); return Data() }

        

var tx = Sawtooth_Transaction_Transaction()

        tx.header = txHeaderData

        tx.headerSignature = sign(data: txHeaderData)

tx.payload = encodedPayload


var batchHeader = Sawtooth_Transaction_BatchHeader()

batchHeader.signerPublicKey = key.publicKey().description

batchHeader.transactionIds = [tx.headerSignature]

var batchHeaderData = Data()

        do { try batchHeaderData = batchHeader.serializedData() }

        catch { errorList += ("Error: \(error)" + "\n"); return Data() }

        let batchHeaderSignature = sign(data: batchHeaderData)


var batch = Sawtooth_Transaction_Batch()

        do { try batch.header = batchHeader.serializedData() }

        catch { errorList += ("Error: \(error)" + "\n"); return Data() }

batch.headerSignature = batchHeaderSignature

batch.transactions = [tx]


var batchList = Sawtooth_Transaction_BatchList()

batchList.batches = [batch]

        var batchListData: Data = Data()

        do { try batchListData = batchList.serializedData() }

        catch { errorList += ("Error: \(error)" + "\n"); return Data() }

        return batchListData

}

    

    func sign(data: Data) -> String {

        let hash = Crypto.sha256(data)

        var signedHash = Data()

        do { try signedHash = Crypto.sign(hash, privateKey: key)

        } catch { errorList += ("Error: \(error)" + "\n") }

        return signedHash.hexEncodedString()

    }

    

    func sha512Hex(string: String) -> String {

        var digest = [UInt8](repeating: 0, count: Int(CC_SHA512_DIGEST_LENGTH))

        if let data = string.data(using: String.Encoding.utf8) {

            let value =  data as NSData

            CC_SHA512(value.bytes, CC_LONG(data.count), &digest)

        }

        var digestHex = ""

        for index in 0..<Int(CC_SHA512_DIGEST_LENGTH) {

            digestHex += String(format: "%02x", digest[index])

        }

        return digestHex

    }

    

    func sha512Hex(data:Data) -> String? {

        var hashData = Data(count: Int(CC_SHA512_DIGEST_LENGTH))

        _ = hashData.withUnsafeMutableBytes {digestBytes in

            data.withUnsafeBytes {messageBytes in

                CC_SHA512(messageBytes, CC_LONG(data.count), digestBytes)

            }

        }

        return hashData.map { String(format: "%02hhx", $0) }.joined()

    }

}
